// Функция отображения топ 100 рейтинга, selfRating - позиция участника которую нужно выделить фиолетовым цветом
function showRating(selfRating=0) {
    $(".rating__container").empty()

    // Первым трём пользователям ставим значок топ и медальку
    let i = 1
    for (let user of ratingData) {
        // Пропускаем если не прошел проверку
        if (!validateName(user.nickname)) {
            continue
        }

        // Скорость прохождения теста
        let timekeepingSplit = user.timekeeping.split(":")
        $(".rating__container").append(`
            <div class="rating__user">
                <div class="rating__user-info">
                    <div class="rating__user-position" data-position="${i}"></div>
                    <div class="rating__user-name">${user.nickname}</div>
                    ${i+1 <= 3 ? `<img class="rating__user-medal" src="https://static.tildacdn.com/tild6434-3139-4165-b238-383132613163/card-medal.png" alt="rating-medal-icon">` : ""}
                    <div class="rating__user-percent"><span>${timekeepingSplit[1]} мин. ${timekeepingSplit[2]} сек.</span> ${user.grade}%</div>
                </div>
                <div class="rating__user-progressbar-bg">
                    <div class="rating__user-progressbar" style="width: ${user.grade}%; ${i === selfRating ? "background-color: #6943D9;" : ""}"></div>
                </div>
            </div>
        `)
        i++
    }
}